mapper.select:
select pid, mid, pname, pkind, pbirth, pgender, pfile, psfile
from pcp_pet
where pid=#{pid}

mapper.update:
update pcp_pet
set pname=#{pname}, pkind=#{pkind}, pbirth=#{pbirth}, pgender=#{pgender},
    pfile=#{pfile, jdbcType=VARCHAR}, psfile=#{psfile, jdbcType=VARCHAR}
where pid=#{pid}
